# Define the load_monsters_from_text function first
def load_monsters_from_text(text_data):
    monster_list = []
    monster_blocks = re.findall(r'{([^}]+?)}', text_data, re.DOTALL) 
    
    for block_content in monster_blocks:
        monster_dict = {}
        pairs = re.split(r',\s*(?=[\w\s\.]*:\s*)', block_content)

        for pair in pairs:
            if ':' in pair:
                key, value = pair.split(':', 1) # Split only on the first colon
                monster_dict[key.strip()] = value.strip()
        monster_list.append(monster_dict)
    return monster_list


