def get_all_biomes(monster_list):
    biomes = set()
    for m in monster_list:
        for i in range(1, 6):
            biome = m.get(f'Biome {i}', '').strip()
            if biome:
                biomes.add(biome)
    return sorted(biomes)

def get_all_crs(monster_list):
    crs = set()
    for m in monster_list:
        cr = m.get('CR', '').strip()
        if cr:
            crs.add(cr)
    return sorted(crs, key=lambda x: float(x) if x.replace('.', '', 1).isdigit() else x)

